var newsArr = [];
function setValueInTable() {
  var table = document.getElementById("table"),
    news = document.getElementById("newsInput"),
    category = document.getElementsByName("newsType"),
    createdTr = document.createElement("tr"),
    createdTdOfNews = document.createElement("td");
  createdTdOfNews.innerText = news.value;
  var createdTdOfCategory = document.createElement("td");
  (createdTdOfCategory.innerText = category[getChecked(category)].value),
    createdTr.appendChild(createdTdOfNews),
    createdTr.appendChild(createdTdOfCategory),
    table.appendChild(createdTr),
    newsArr.push(createdTdOfNews.innerText),
    console.log(newsArr);
}
function getChecked(arr) {
  for (var index = 0, i = 0; i < arr.length; i++)
    arr[i].checked ? (index = i) : alert("asd");
  return index;
}
